import React from 'react';

function handleClick() {
  console.log('Clicou no botão!');
}

class App extends React.Component {
  render() {
    return (<button onClick={ handleClick }>Botão</button>);
  }
}

export default App;


<div className="App">
     <button className="button1">BOTAO 1</button>
     <button className="button2">BOTAO 2</button>
     <button className="button3">BOTAO 3</button>
    </div>
