import { Component } from 'react';
import './App.css'


class App extends Component {
  constructor() {
    super();
    this.state = {
      infoUser: undefined,
      loading: true,
    };
  }

  async fetchApi() {
    const requestReturn = await fetch(`https://api.randomuser.me/`);
    const requestObj = await requestReturn.json();
    this.setState({
      loading: false,
      infoUser: requestObj
    });
  }

  componentDidMount() {
    this.fetchApi();
  }

    render() {
      const { infoUser } = this.state;
      return (
        <div>
          <span>
            <h1>Random User</h1>
            <p>{infoUser}</p>
          </span>
        </div>
      );
    }
}

export default App;
